mapper.select:
SELECT u.id, u.telephone, u.`password`, u.nickname, u.avatar, u.type, u.balance, u.is_valid isValid, u.remark, u.create_time createTime, u.update_time updateTime, s.telephone agentMobile FROM users u LEFT JOIN ( SELECT a.user_id, ur.telephone FROM user_agent a, users ur WHERE a.agent_user_id = ur.id ) s ON u.id = s.user_id where 1=1
and u.id=#{userId}
and u.type=#{type}
and u.is_valid=#{isValid}
and u.telephone = #{mobile}
and u.create_time >= #{startTime}
and u.create_time <= #{endTime}
order by u.type

SELECT r.id, r.user_agent_id userAgentId, r.user_id userId, r.amount, r.create_time createTime, u.telephone mobile, u.nickname FROM charge_record r, users u WHERE r.user_agent_id = u.id
and u.telephone = #{mobile}
order by r.id desc

SELECT r.id, r.user_agent_id userAgentId, r.user_id userId, r.amount, r.create_time createTime, u.telephone mobile, u.nickname FROM withdraw_record r, users u WHERE r.user_agent_id = u.id
and u.telephone = #{mobile}
order by r.id desc
